import logger from 'morgan';
import express from 'express';
import cookieParser from 'cookie-parser';
import indexRouter from '.';
import {session_secret} from './config'
import cors from 'cors'
import session from 'express-session';
import RedisStore from 'connect-redis';
import { createClient } from 'redis';
import { mongo_cluster } from './config';
import { MongoClient } from 'mongodb';

const app = express();
  let redisClient = createClient();
//  redisClient.connect().catch(console.error);
//  export let redisStore = new RedisStore({
//    client: redisClient,
//  });

app.use(cors({
  origin: '*'
}));

const options = {
  useNewUrlParser: true,
  useUnifiedTopology: true,
};

export let mongoClient;

async function connectToCluster(){
  try {
      mongoClient = new MongoClient(mongo_cluster);
      console.log('Connecting to MongoDB Atlas cluster...');
      await mongoClient.connect();
      console.log('Successfully connected to MongoDB Atlas!');
      return mongoClient;
  } catch (error) {
      console.error('Connection to MongoDB Atlas failed!', error);
  }
}

connectToCluster();

process.on('SIGINT', async () => {
  try {
    // Fermer la connexion MongoDB
    await mongoClient.close();
    console.log('Déconnexion de MongoDB');
    process.exit(0);
  } catch (error) {
    console.error('Erreur lors de la déconnexion de MongoDB:', error);
    process.exit(1);
  }
});

app.use(logger('dev'));
app.use(express.json());
app.use(express.urlencoded({ extended: true }));
app.use(cookieParser());
app.use(session({
  secret: `${session_secret}`,
  resave: false,
 // cookie: {secure: true},
  saveUninitialized: false,
 /// store: redisStore
}));

//app.use('/api/v1/admin/', islogged);
//app.use('/api/v1/admin/', ConnectAdmin);
//app.use('/api/v1/area/', islogged);
//app.use('/api/v1/service/', islogged);
//app.use('/api/v1/logout', islogged);
//app.use('/api/v1/reset/password', islogged);
app.use('/', indexRouter);

export default app;

