import {addUserInCo, changePass, findUserWithMail } from '../dataBase/dataBase.js';
import { sign,  verify } from 'jsonwebtoken';
import {isPasword} from './tools.js';

export async function signup(req, res)
{
    if (!req.body.email) res.status(400).json({err: "Email don't given"});
    else if (!req.body.password) res.status(400).json({err: "Password don't given"}); 
    else {
        try {
            let status = await isRegistered(req);
            if (!status)  res.status(400).json({err: "User doesn't exist"});
             else {
                 let user = await findUserWithMail(req.body.email);
                 let isSame = await isPasword(req.body.password, user[0].password);
                 if (!isSame) res.status(400).json({err: "password Incorrect"});
                 else {
                     const token = sign({id: user[0]._id.toString()}, 'RANDOM_TOKEN_SECRET', {expiresIn: '24h'});        
                     req.session._id = user[0]._id;
                     req.session.profile = user[0].Profile;
                     req.session.isAdmin = user[0].isAdmin;
                     req.session.isHead = user[0].isHead;
                     req.session.section = user[0].section;
                     req.session.token = token;
                    res.status(200).json({message: 'you are login sucessfully!!!'});
                }
        }
        } catch(error) {
            res.status(400).json({err: "Une erreur s'est produite. Reessayez"});
        }
    }
}

export async function register(req, res)
{
    if (!req.body.email) res.status(400).json({err: "Username don't given"});
    else if (!req.body.password) res.status(400).json({err: "Password don't given"}); 
        else if (!req.body.profile) res.status(400).json({err: "Profile don't given"});
    else {
        try {
            let status = await isRegistered(req);
            if (!status) {
                await addUserInCo(req.body.email, req.body.password, req.body.admin, req.body.profile);
                res.status(200).json({message: "The registration is a success !!"});
            } else res.status(400).json({err: "user already registered"});
        } catch(error) {
            res.status(400).json({err: "Une erreur s'est produite. Reessayez"})
        }
    }
}

export async function changePassword(req, res)
{
    console.log("ojee");
    if (!req.body.email) res.status(400).json({err: "Username don't given"});
    else if (!req.body.password) res.status(400).json({err: "Password don't given"}); 
    else {
        try {
            let status = await isRegistered(req);
            if (status) {
                await changePass(req.body.email, req.body.password);
                res.status(200).json({message: "Password changed with success !!"});
            } else res.status(400).json({err: "user doesn't exist"});
        } catch(error) {
            res.status(400).json({err: "Une erreur s'est produite. Reessayez"})
        }
    }
}

export function tokenIsValid(token)
{
    let status = false;
    verify(token, 'RANDOM_TOKEN_SECRET', function (err, decode) {
    if (err) {
       // console.log(err);
        status = false;
    } else {
       // console.log(decode);
       // console.log("yoooo");
        status =  true;
    }
    });
    return status;
}

export async function islogged(req, res)
{
    console.log(req.session.token);
    if (!req.session.token) {
        res.status(200).json({islogged: false});
    }
    else {
        if (!tokenIsValid(req.session.token)) {
            res.status(200).json({islogged: false});
        }
        else {
            res.status(200).json({islogged: true});
        }
    }
}

export async function isRegistered(req)
{
    let user = await findUserWithMail(req.body.email);
    if (user.length == 0) {
        return false;
    }
    return true;
}

