export async function checkUrgencyCommand(sectionName)
{
    let mongoClient
    let commands = [];

    try {
        mongoClient = await connectToCluster();
        const db = mongoClient.db('cO');
        const collection = db.collection(sectionName);
        let doc = await collection.find({}).toArray();
        for(let i = 0; i != doc.length; i++) {
            for (let j = 0; j != doc[i].commandName.length; j++) {
                const name = doc[i].commandName[j];
                const docCommand = doc[i][name];
                const date1 = new Date();
                console.log(date1);
                if (docCommand.flag == 'red')commands.push(docCommand);
            }
        }
    }  catch(error) {
        console.log("Error: ", error);
       }finally {
        await mongoClient.close();
    }
    return commands;
}

