import { hash } from "bcrypt";
import { compare } from "bcrypt";
import { tokenIsValid } from "./login";
import nodemailer from 'nodemailer'
import cron from 'node-cron'
import { isMonday, startOfWeek, endOfWeek, isWithinInterval, isFriday } from 'date-fns';
import { getAgencyFuturCommand, getAgencyTodayCommand, getAgencyTreatCommand, getAgencyUrgencyCommand, getAllDoneCommand, getAllTreatementCommand, getAllUrgencyCommand, getAlltodayCommand, modifyCommand } from "../dataBase/dataBase";
import { co_pass } from "../config";
export let tab = [];

export class Mail {
    constructor(settings ) {

        this.settings = settings;
       // console.log(settings);
        this.options = {
            from: 'cocommande@co-agency.co',
            to: '',
            subject: '',
            text: '',
            html: ''
           // html: "<p style='font-weight:bold; font-size:25px;'> Hi. My name is John </p>"
        };
    }

    async  send(to, subject, body) {
        if(nodemailer && this.options) {
            let self = this;
           // console.log(self.settings);
            const transporter = nodemailer.createTransport(self.settings);
