export async function hashPassword(password) {
    const saltRounds = 10;
    const hashedPassword = await hash(password, saltRounds);
    return hashedPassword;
}

export async function isPasword(password, hashedpassword)
{
    const status = await compare(password, hashedpassword);
    return status;
}

export async function sentMail(message, email, subject)
{
    const mail = new Mail({
        host: 'smtp.dreamhost.com',
        port: 465,
        secure: true,
        auth: {
          user: 'cocommande@co-agency.co',
          pass: co_pass,
        }
      });
      await mail.send(email, subject, message)
}


function isWeekday(date) {
    const startOfCurrentWeek = startOfWeek(new Date(), { weekStartsOn: 1 });
    console.log(startOfCurrentWeek);
    const endOfCurrentWeek = endOfWeek(new Date(), { weekStartsOn: 1 });
    console.log(endOfCurrentWeek);
    return isWithinInterval(date, { start: startOfCurrentWeek, end: endOfCurrentWeek });
}

export async function getSectionStatitic(req, res)
{
    if (!req.body.sectionName) res.status(400).json({err: "Section name don't given"});
    let commands = [];
    const currentDate = new Date();
    let done = [];
    if (isMonday(currentDate)) {
        tab.splice(0, tab.length);
    let doneCommand = await getAllDoneCommand(req.body.sectionName);
    let urgencyCommand = await getAllUrgencyCommand(req.body.sectionName);
    let treatCommand = await getAllTreatementCommand(req.body.sectionName);
    let todayCommand = await getAlltodayCommand(req.body.sectionName);
    for (let i = 0; i != doneCommand.length; i++) commands.push(doneCommand[i]);
    for (let i = 0; i != urgencyCommand.length; i++) commands.push(urgencyCommand[i]);
    for (let i = 0; i != treatCommand.length; i++) commands.push(treatCommand[i]);
    for (let i = 0; i != todayCommand.length; i++) commands.push(todayCommand[i]);
    for (let i = 0; i != commands.length; i++) {
        if (isWeekday(commands[i].deadLine)) tab.push(commands[i]);
    }
    } else {
        let doneCommand = await getAllDoneCommand(req.body.sectionName);
        for (let i = 0; i != doneCommand.length; i++) {
            if (isWeekday(doneCommand[i].deliveryDate)) done.push(doneCommand[i])
        }
    }
    let percent = 0;
    if (tab.length == 0)
        percent = 0;
    else percent = (done.length * 100) / tab.length;
    res.status(200).json({percent: percent})
}

