export async function modifyCommandDate(req, res)
{
    if (!req.body.sectionName) res.status(400).json({err: "section name don't given"});
    else if (!req.body.clientName) res.status(400).json({err: "client name don't given"});
    else if (!req.body.commandName) res.status(400).json({err: "command name don't given"});
    else if (!req.body.date) res.status(400).json({err: "date don't given"});
    else if (!req.body.workManager) res.status(400).json({err: "manager don't given"});
    else {
        try {
            let message = `<p style='font-weight:bold; font-size:20px;'>\n\n La date de livraison de la commande ${req.body.commandName} de la marque ${req.body.clientName} a été modifiée.\n\n\ Rendez vous sur le site: https://co-commande.onrender.com pour plus de détails!!</p>`;
            const date = new Date(req.body.date);
            const optionsDay = { weekday: 'long' };
            const dayName = date.toLocaleDateString('fr-FR', optionsDay);
            const dayNumber = date.getDate();
            const monthNumber = date.getMonth() + 1;
            const monthNames = [
              'Janvier', 'Février', 'Mars', 'Avril', 'Mai', 'Juin',
              'Juillet', 'Août', 'Septembre', 'Octobre', 'Novembre', 'Décembre'
            ];
            const monthName = monthNames[date.getMonth()];
            const year2 = date.getFullYear();
            const formattedDate = `${dayName} ${dayNumber} ${monthName} ${year2}`;
            const updating = {
                deadLine: date,
                date: formattedDate,
            }
            await modifyDate(req.body.sectionName, req.body.clientName, updating, req.body.commandName);
           let user = await findUserWithProfile(req.body.workManager);
            await sentMail(message, user[0].mail, 'Date de livraison modifiée');
           res.status(200).json({message: "command modified"});
        } catch(error) {
            res.status(400).json({err:  "Une erreur s'est produite. Reessayez"});
        }
    }
}


