import express from 'express';
import { changePassword, islogged, register, signup } from './routes/login';
import { addClient, addCommand, deleteClient, deleteCommand, getAgencyDone, getAgencyFutur, 
getAgencyNumberDone, getAgencyNumberFutur, getAgencyNumberToday, getAgencyNumberTreat, 
getAgencyNumberUrgency, getAgencyToday, getAgencyTreat, getAgencyUrgency, getAllClient, 
getAllCommand, getClientDoneCommand, getClientFuturCommand, getClientTodayCommand,
getClientTreatementCommand, getClientUrgencyCommand, getDoneCommand, getFuturCommand,
getGeneralNumberDone, getGeneralNumberFutur, getGeneralNumberToday,
getGeneralNumberTreat, getGeneralNumberUrgency, getNumberDone,getNumberFutur,
getNumberToday,getNumberTreatement,getNumberUrgency, getSectionEmployee, getTask, getTodayCommand, getTreatementCommand, 
getUrgencyCommand,modifyCommandDate,modifytypeCommand, } from './routes/sections';
import { deleteUser, getProfile, isAdmin, isHead } from './routes/user';
import { getSectionStatitic} from './routes/tools';
import cron from 'node-cron'
import axios from 'axios';
const indexRouter = express.Router();

indexRouter.get('/', async function (req, res) {
      if (req.session.view) {
          req.session.view++;
          res.send("You visited this page for "
              + req.session.view + " times");
      }
      else {
          req.session.view = 1;
        res.send("You have visited this page"
           + " for first time ! Welcome....");
    }
    req.session.save()
}); 

cron.schedule('*/2 * * * *', async () => {
    const config = {
        headers: {
          'Content-Type': 'application/json',
        },
    };
    try {
      const response = await axios.get('https://co-commande.onrender.com/',  config);
      console.log('Requête GET effectuée:', response.data);
    } catch (error) {
      console.error('Erreur lors de la requête GET:', error);
    }
  });

indexRouter.get('/ok', (req, res) => {
    res.status(200).json({ message: "ok" });
});

indexRouter.post('/login', (req, res) => signup(req, res));
indexRouter.post('/getEmployee', (req, res) => getSectionEmployee(req, res));
indexRouter.post('/changePassword', (req, res) => changePassword(req, res));
indexRouter.post('/register', (req, res) => register(req, res));
indexRouter.post('/user/delete', (req, res) => deleteUser(req, res));
indexRouter.post('/sections/addClient', (req, res) => addClient(req, res));
indexRouter.post('/sections/addClientCommand', (req, res) => addCommand(req, res));
indexRouter.post('/sections/deleteClient', (req, res) => deleteClient(req, res));
indexRouter.post('/sections/deleteClientCommand', (req, res) => deleteCommand(req, res));
indexRouter.post('/sections/getAllClient', (req, res) => getAllClient(req, res));
indexRouter.post('/sections/getAllClientCommand', (req, res) => getAllCommand(req, res));
indexRouter.get('/user/getProfile', (req, res) => getProfile(req, res));
indexRouter.get('/user/isAdmin', (req, res) => isAdmin(req, res));
indexRouter.post('/user/isHead', (req, res) => isHead(req, res));
indexRouter.get('/islogged', (req, res) => islogged(req, res));
indexRouter.post('/sections/getStatistic', (req, res) => getSectionStatitic(req, res));
indexRouter.post('/sections/getUrgencyCommand', (req, res) => getUrgencyCommand(req, res));
indexRouter.post('/sections/getTreatementCommand', (req, res) => getTreatementCommand(req, res));
indexRouter.post('/sections/getFuturCommand', (req, res) => getFuturCommand(req, res));
indexRouter.post('/sections/getTodayCommand', (req, res) => getTodayCommand(req, res));
indexRouter.post('/sections/getDoneCommand', (req, res) => getDoneCommand(req, res));
indexRouter.post('/sections/client/getNumberDone', (req, res) => getNumberDone(req, res));
indexRouter.post('/sections/client/getNumberFutur', (req, res) => getNumberFutur(req, res));
indexRouter.post('/sections/client/getNumberTreat', (req, res) => getNumberTreatement(req, res));
indexRouter.post('/sections/client/getNumberUrgency', (req, res) => getNumberUrgency(req, res));
indexRouter.post('/sections/client/getNumberToday', (req, res) => getNumberToday(req, res));
indexRouter.post('/sections/getNumberToday', (req, res) => getGeneralNumberToday(req, res));
indexRouter.post('/sections/getNumberFutur', (req, res) => getGeneralNumberFutur(req, res));
indexRouter.post('/sections/getNumberUrgency', (req, res) => getGeneralNumberUrgency(req, res));
indexRouter.post('/sections/getNumberDone', (req, res) => getGeneralNumberDone(req, res));
indexRouter.post('/sections/getNumberTreat', (req, res) => getGeneralNumberTreat(req, res));
indexRouter.post('/sections/client/getDoneCommand', (req, res) => getClientDoneCommand(req, res));
indexRouter.post('/sections/client/getFuturCommand', (req, res) => getClientFuturCommand(req, res));
indexRouter.post('/sections/client/getTodayCommand', (req, res) => getClientTodayCommand(req, res));
indexRouter.post('/sections/client/getUrgencyCommand', (req, res) => getClientUrgencyCommand(req, res));
indexRouter.post('/sections/client/getTreatementCommand', (req, res) => getClientTreatementCommand(req, res));
indexRouter.post('/sections/getAgencyTreatCommand', (req, res) => getAgencyTreat(req, res));
indexRouter.post('/sections/getAgencyDoneCommand', (req, res) => getAgencyDone(req, res));
indexRouter.post('/sections/getAgencyFuturCommand', (req, res) => getAgencyFutur(req, res));
indexRouter.post('/sections/getAgencyUrgencyCommand', (req, res) => getAgencyUrgency(req, res));
indexRouter.post('/sections/getAgencyTodayCommand', (req, res) => getAgencyToday(req, res));
indexRouter.post('/sections/getNumberAgencyToday', (req, res) => getAgencyNumberToday(req, res));
indexRouter.post('/sections/getNumberAgencyFutur', (req, res) => getAgencyNumberFutur(req, res));
indexRouter.post('/sections/getNumberAgencyDone', (req, res) => getAgencyNumberDone(req, res));
indexRouter.post('/sections/getNumberAgencyUrgency', (req, res) => getAgencyNumberUrgency(req, res));
indexRouter.post('/sections/getNumberAgencyTreat', (req, res) => getAgencyNumberTreat(req, res));
indexRouter.post('/sections/modifyCommand', (req, res) =>  modifytypeCommand(req, res));
indexRouter.post('/sections/modifyCommandDate', (req, res) =>  modifyCommandDate(req, res));
indexRouter.post('/sections/getTask', (req, res) => getTask(req, res));
export default indexRouter;

