export async function getAllUrgencyCommand(sectionName)
{
    let commands = [];

    const db = mongoClient.db(`${sectionName}`);
    const collections = await db.listCollections().toArray();
    for (const collection of collections) {
        const collectionName = collection.name;
        let urgencyClientCommand = await db.collection(collectionName).find({type: "Urgente"}).toArray();
        for (let i = 0; i < urgencyClientCommand.length; i++)
        commands.push(urgencyClientCommand[i]);
