    const sections = ['cO_Edition', 'cO_Event', 'cO_Marketing', 'cO_Conception', 'cO_Impression', 'cO_Communication'];
    let commands = [];
    for (let j = 0; j != sections.length; j++) {
        let command = await getAllUrgencyCommand(sections[j]);
        for (let i = 0; i < command.length; i++)
        commands.push(command[i]);
