import {MongoClient, ObjectId} from "mongodb";
import { mongo_cluster } from "../config";
import { hashPassword } from "../routes/tools";
import { mongoClient } from "../app";
let test = 0;



export async function getAllUser()
{
   let users;
   try {
       const db = mongoClient.db('cO');
       const collection = db.collection('USERS');
       users = await collection.find({}).toArray();
   } catch(error) {
    console.log("Error: ", error);
   }
   return users;
}

 export async function getAllClientofSection(sectionName)
 {
    let clients;

    const db = mongoClient.db(`${sectionName}`);
    clients = await db.listCollections().toArray();
    return clients;
 }

 export async function getAllClientCommand(sectionName, clientName)
 {
    let commands;

    const db = mongoClient.db(`${sectionName}`);
    const collection = db.collection(clientName);  
    commands = await collection.find({}).toArray();
    return commands;
}

export async function getAllUrgencyCommand(sectionName)
{
    let commands = [];

    const db = mongoClient.db(`${sectionName}`);
    const collections = await db.listCollections().toArray();
    for (const collection of collections) {
        const collectionName = collection.name;
        let urgencyClientCommand = await db.collection(collectionName).find({type: "Urgente"}).toArray();
        for (let i = 0; i < urgencyClientCommand.length; i++)
        commands.push(urgencyClientCommand[i]);
    }
    return commands;
}

