export async function modifyCommand(sectionName, clientName, updating, commandName)
{
    const db = mongoClient.db(`${sectionName}`);
    const collection = await db.collection(clientName);
    await collection.updateOne(
        {commandName: commandName},
        {$set: updating}
    );
}



export async function modifyDate(sectionName, clientName, updating, commandName)
{
    const db = mongoClient.db(`${sectionName}`);
    const collection = await db.collection(clientName);
