        mongoClient = await connectToCluster();
        const db = mongoClient.db('cO');
        const collection = db.collection(sectionName);
        let doc = await collection.find({}).toArray();
        tasksDone = doc.numberOfTaskDone;
    } finally {
        await mongoClient.close();
    }
    return tasksDone;
}

export async function addUserInCo(email, password, admin, profile) {
    const db = mongoClient.db('cO');
    const collection = db.collection('USERS');
    await addUser(collection, email, password, admin, profile);
}

export async function changePass(email, password) {
    const db = mongoClient.db('cO');
    const collection = db.collection('USERS');
    let hashedPass = await hashPassword(password);
    let updating = {
        password: hashedPass
    }
    await collection.updateOne(
        {mail: email},
        {$set: updating}
    );
}

 export async function addUser(collection, email, passWord, admin, profile) {
    let hashedPass = await hashPassword(passWord)
    const User = {
        password: hashedPass,
        mail: email,
        isAdmin: admin,
        Profile: profile
    };
    await collection.insertOne(User);
 }


 export async function updateData(collection, name, updating)
{
   await collection.updateOne(
    {name},
    {$set: updating}
   );
}


export async function addClientFolder(sectionName, clientName)
{
    const db = mongoClient.db(`${sectionName}`);
    const collection = await db.createCollection(clientName);
}

export async function addClientCommand(sectionName, clientName, command)
{
    const db = mongoClient.db(`${sectionName}`);
    const collection = db.collection(clientName);
    await collection.insertOne(command);
}

export async function deleteClientCommand(sectionName, clientName, commandName)
{
    const db = mongoClient.db(`${sectionName}`);
    const collection = db.collection(clientName);
    await collection.deleteOne({commandName: commandName});
}

export async function deleteClientFolder(sectionName, clientName)
{
    const db = mongoClient.db(`${sectionName}`);
    await db.collection(clientName).drop();
}

export async function deleteUserInCo(email)
{
    mongoClient = await connectToCluster(uri);
    const db = mongoClient.db('cO');
    const collection = db.collection('USERS');
    await collection.deleteOne({mail: email});
}

