sourcesContent:
import { addClientFolder, addClientCommand, deleteClientFolder, deleteClientCommand,
getAllClientofSection, getAllClientCommand, getAllUrgencyCommand, getNumberOfTask, getNumberOfTaskDone,
getAllTreatementCommand,getAllFutureCommand,getAllDoneCommand,getAlltodayCommand,getAllClientDoneCommand,
getAllClientTodayCommand,getAllClientFuturCommand,getAllClientUrgencyCommand,getAllClientTreatementCommand,
modifyCommand,getNumberClientDoneCommand,getNumberClientUrgencyCommand,getNumberClientFuturCommand,
getNumberClientTreatementCommand,getNumberClientTodayCommand,getNumberUrgencyCommand,getNumberDoneCommand,
getNumberTodayCommand,getNumberTreatCommand,getNumberFuturCommand,getAgencyTreatCommand,
getAgencyDoneCommand,getAgencyUrgencyCommand,getAgencyTodayCommand,getAgencyFuturCommand,
getAgencyNumberDoneCommand,getAgencyNumberUrgencyCommand,getAgencyNumberTodayCommand,getAgencyNumberFuturCommand,
getAgencyNumberTreatCommand, findUserWithMail, findUserWithProfile,
modifyDate, getEmployee} from "../dataBase/dataBase";
import {sentMail } from "./tools";

export async function addClient(req, res)
{
    if (!req.body.sectionName) res.status(400).json({err: "section name don't given"});
    else if (!req.body.clientName) res.status(400).json({err: "client name don't given"});
    else {
        try {
        await addClientFolder(req.body.sectionName, req.body.clientName);
        res.status(200).json({message: "Client Folder added"});
        } catch(error) {
            res.status(400).json({err: "Une erreur s'est produite. Reessayez"});
        }
    }
}

export async function addCommand(req, res)
{
    if (!req.body.sectionName) res.status(400).json({err: "section name don't given"});
    else if (!req.body.clientName) res.status(400).json({err: "client name don't given"});
    else if (!req.body.section) res.status(400).json({err: "section name don't given"});
    else if (!req.body.commandName) res.status(400).json({err: "command name don't given"});
    else if (!req.body.workManager) res.status(400).json({err: "workManager don't given"});
    else if (!req.body.deadLine) res.status(400).json({err: "deadline date don't given"});
    else if (!req.body.description) res.status(400).json({err: "description don't given"});
    else if (!req.body.type) res.status(400).json({err: "flag don't given"});
    else {
        try{
            let message = `<p style='font-weight:bold; font-size:20px;'>\n\nUne nouvelle tâche ajoutée vous est attribuée pour la marque ${req.body.clientName}.\n\n\ Rendez vous sur le site: https://co-commande.onrender.com pour plus de détails!!</p>`;
              console.log(req.body.deadLine);
            const date = new Date(req.body.deadLine);
            const optionsDay = { weekday: 'long' };
            const dayName = date.toLocaleDateString('fr-FR', optionsDay);
            const dayNumber = date.getDate();
            let manager = req.body.workManager;
            req.body.workManager.join(', ');
            console.log(req.body.workManager)
            const monthNames = [
              'Janvier', 'Février', 'Mars', 'Avril', 'Mai', 'Juin',
              'Juillet', 'Août', 'Septembre', 'Octobre', 'Novembre', 'Décembre'
            ];
            const monthName = monthNames[date.getMonth()];
            const year2 = date.getFullYear();
            const formattedDate = `${dayName} ${dayNumber} ${monthName} ${year2}`;
            console.log(req.body.workManager[1])
            const Command = {
                workManager: req.body.workManager,
                deadLine: date,
                date: formattedDate,
                client: req.body.clientName,
                type: req.body.type,
                description: req.body.description,
                commandName: req.body.commandName,
                section: req.body.section,
            }
            await addClientCommand(req.body.sectionName, req.body.clientName, Command);
            if (manager.length == 0) res.status(400).json({err: "Selectionnez un profil"});
            else {
                for (let i = 0;  i != manager.length; i++) {
                let user = await findUserWithProfile(manager[i]);
                    await sentMail(message, user[0].mail, 'Nouvelle tâche');
                }
            }
            res.status(200).json({message: "Client command added"});
        } catch(error) {
             res.status(400).json({err: "Une erreur s'est produite. Reessayez"});
         }
    }
}


export async function deleteClient(req, res)
{
    if (!req.body.sectionName) res.status(400).json({err: "section name don't given"});
    else if (!req.body.clientName) res.status(400).json({err: "client name don't given"});
    else {
        try {
            await deleteClientFolder(req.body.sectionName, req.body.clientName);
            res.status(200).json({message: "Client deleted"});
        } catch(error) {
            res.status(400).json({err:  "Une erreur s'est produite. Reessayez"});
        }
    }
}

export async function modifytypeCommand(req, res)
{
    if (!req.body.sectionName) res.status(400).json({err: "section name don't given"});
    else if (!req.body.clientName) res.status(400).json({err: "client name don't given"});
    else if (!req.body.commandName) res.status(400).json({err: "command name don't given"});
    else if (!req.body.type) res.status(400).json({err: "type don't given"});
    else {
        try {
            let updating;
            if (req.body.type == "Terminer") {
                const currentDate = new Date();
                updating = {
                    type: req.body.type,
                    deliveryDate: currentDate
                }
            } else {
                updating = {
                    type: req.body.type
                }
            }
            await modifyCommand(req.body.sectionName, req.body.clientName, updating, req.body.commandName);
            res.status(200).json({message: "command modified"});
        } catch(error) {
            res.status(400).json({err:  "Une erreur s'est produite. Reessayez"});
        }
    }
}

export async function modifyCommandDate(req, res)
{
    if (!req.body.sectionName) res.status(400).json({err: "section name don't given"});
    else if (!req.body.clientName) res.status(400).json({err: "client name don't given"});
    else if (!req.body.commandName) res.status(400).json({err: "command name don't given"});
    else if (!req.body.date) res.status(400).json({err: "date don't given"});
    else if (!req.body.workManager) res.status(400).json({err: "manager don't given"});
    else {
        try {
            let message = `<p style='font-weight:bold; font-size:20px;'>\n\n La date de livraison de la commande ${req.body.commandName} de la marque ${req.body.clientName} a été modifiée.\n\n\ Rendez vous sur le site: https://co-commande.onrender.com pour plus de détails!!</p>`;
            const date = new Date(req.body.date);
            const optionsDay = { weekday: 'long' };
            const dayName = date.toLocaleDateString('fr-FR', optionsDay);
            const dayNumber = date.getDate();
            const monthNumber = date.getMonth() + 1;
            const monthNames = [
              'Janvier', 'Février', 'Mars', 'Avril', 'Mai', 'Juin',
              'Juillet', 'Août', 'Septembre', 'Octobre', 'Novembre', 'Décembre'
            ];
            const monthName = monthNames[date.getMonth()];
            const year2 = date.getFullYear();
            const formattedDate = `${dayName} ${dayNumber} ${monthName} ${year2}`;
            const updating = {
                deadLine: date,
                date: formattedDate,
            }
            await modifyDate(req.body.sectionName, req.body.clientName, updating, req.body.commandName);
           let user = await findUserWithProfile(req.body.workManager);
            await sentMail(message, user[0].mail, 'Date de livraison modifiée');
           res.status(200).json({message: "command modified"});
        } catch(error) {
            res.status(400).json({err:  "Une erreur s'est produite. Reessayez"});
        }
    }
}


