import {deleteUserInCo} from "../dataBase/dataBase";

export async function deleteUser(req, res)
{
    if (!req.body.email) res.status(400).json({err: "Email don't given"});
    else {
        try {
        await deleteUserInCo(req.body.email);
        res.status(200).json({message: "User are deleted!!"});
        } catch(error) {
            res.status(400).json({err: "Une erreur s'est produite. Reessayez"})
        }
    }
}

export async function getProfile(req, res)
{ 
    console.log(req.session.profile);
    res.status(200).json({profile: req.session.profile})
}

export async function isAdmin(req, res)
{
    if (req.session.isAdmin) res.status(200).json({adminOption: {
        isAdmin: true,
        section: req.session.section
    }  });
    else res.status(200).json({adminOption: {
        isAdmin: false,
        section: req.session.section
    }});
}

export async function isHead(req, res)
{ 
    if (!req.body.sectionName) res.status(400).json({err: "section name don't given"});
   // console.log(req.session.isHead);
    if (req.session.isHead && req.session.section == req.body.sectionName) res.status(200).json({isHead: true});
    else res.status(200).json({isHead: false});
}

